import { Component } from '@angular/core';
import { Model, RentACar, User, Car } from "./model";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  model = new Model();

  userId = 0;
  carId = 0;
  rentACarId = 0;

  addUser(user:User){
    this.userId++;
    this.model.addUser(user)
  }
  addCar(car:Car){
    this.carId++;
    this.model.addCar(car)
  }
  addRentACar(rentACar:RentACar){
    this.rentACarId++;
    this.model.addRentACar(rentACar)
  }

  getUserList(){
    return this.model.Users;
  }
  getCarList(){
    return this.model.Cars;
  }
  getRentACarList(){
    return this.model.RentACars;
  }
}
