import { Component } from '@angular/core';
import { Model, RentACar, User, Car } from "./model";
import { FormsModule } from "@angular/forms";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  model = new Model();

  userId = 0;
  carId = 0;
  rentACarId = 0;

  addUser(name: any, surname:any, gender:any, age:any, license:any){
    this.userId++;
    let user = new User(this.userId, name, surname, gender, age, license);
    this.model.addUser(user);
  }
  addCar(car:Car){
    this.carId++;
    car.Id = this.carId;
    this.model.addCar(car)
  }
  addRentACar(rentACar:RentACar){
    this.rentACarId++;
    rentACar.Id = this.rentACarId;
    this.model.addRentACar(rentACar)
  }

  getUserList(){
    return this.model.Users;
  }
  getCarList(){
    return this.model.Cars;
  }
  getRentACarList(){
    return this.model.RentACars;
  }
}
